import React, { useState } from 'react';
import HintUI from './HintUI';
import axios from 'axios';

const QuizUI = ({ questions, onComplete, onProgressUpdate }) => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [evaluation, setEvaluation] = useState(null);
  const [score, setScore] = useState(0);
  const [showHint, setShowHint] = useState(false);

  const handleAnswerSubmit = async () => {
    if (!selectedAnswer) return;

    try {
      const response = await axios.post('http://localhost:8000/evaluate_answer', {
        question: questions[currentQuestion].question,
        user_answer: selectedAnswer,
        correct_answer: questions[currentQuestion].answer
      });

      setEvaluation(response.data);
      setShowResult(true);

      if (response.data.is_correct) {
        setScore(prev => prev + 1);
      }
    } catch (error) {
      console.error('Answer evaluation error:', error);
    }
  };

  const handleNextQuestion = () => {
    const nextQuestion = currentQuestion + 1;

    onProgressUpdate(prev => ({
      ...prev,
      current: nextQuestion,
      score: score
    }));

    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
      setSelectedAnswer('');
      setShowResult(false);
      setEvaluation(null);
      setShowHint(false);
    } else {
      onComplete(score);
    }
  };

  const currentQ = questions[currentQuestion];
  const difficultyColors = {
    easy: 'from-green-500 to-emerald-500',
    medium: 'from-yellow-500 to-orange-500', 
    hard: 'from-red-500 to-pink-500'
  };

  return (
    <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl p-8 border border-white/20">
      <div className="mb-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-white flex items-center gap-2">
            üß© Question {currentQuestion + 1} of {questions.length}
          </h2>
          <span className={`text-sm bg-gradient-to-r ${difficultyColors[currentQ.difficulty] || difficultyColors.medium} text-white px-4 py-2 rounded-full font-semibold shadow-lg`}>
            {currentQ.difficulty.toUpperCase()}
          </span>
        </div>

        <div className="bg-white/5 rounded-2xl p-6 mb-8 border border-white/10">
          <p className="text-xl text-white leading-relaxed">{currentQ.question}</p>
        </div>

        <div className="space-y-4 mb-8">
          {currentQ.options.map((option, index) => (
            <label key={index} className={`flex items-center space-x-4 cursor-pointer p-4 rounded-2xl border-2 transition-all duration-300 ${
              selectedAnswer === option 
                ? 'bg-cyan-500/20 border-cyan-400 shadow-lg' 
                : 'bg-white/5 border-white/20 hover:bg-white/10 hover:border-white/40'
            } ${showResult && option === currentQ.answer ? 'bg-green-500/20 border-green-400' : ''}`}>
              <input
                type="radio"
                name="answer"
                value={option}
                checked={selectedAnswer === option}
                onChange={(e) => setSelectedAnswer(e.target.value)}
                disabled={showResult}
                className="w-5 h-5 text-cyan-400 bg-transparent border-2 border-white/40 focus:ring-cyan-400"
              />
              <span className={`text-white text-lg ${
                showResult && option === currentQ.answer ? 'font-bold text-green-400' : ''
              }`}>
                {option}
              </span>
            </label>
          ))}
        </div>

        <div className="flex gap-4 mb-8">
          {!showResult ? (
            <>
              <button
                onClick={handleAnswerSubmit}
                disabled={!selectedAnswer}
                className="bg-gradient-to-r from-cyan-500 to-blue-500 text-white px-8 py-3 rounded-xl font-semibold hover:shadow-lg transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
              >
                ‚úÖ Submit Answer
              </button>

              <button
                onClick={() => setShowHint(true)}
                className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white px-8 py-3 rounded-xl font-semibold hover:shadow-lg transition-all duration-300 flex items-center gap-2"
              >
                üí° Get Hint
              </button>
            </>
          ) : (
            <button
              onClick={handleNextQuestion}
              className="bg-gradient-to-r from-green-500 to-emerald-500 text-white px-8 py-3 rounded-xl font-semibold hover:shadow-lg transition-all duration-300 flex items-center gap-2"
            >
              {currentQuestion + 1 < questions.length ? '‚û°Ô∏è Next Question' : 'üèÜ Finish Quiz'}
            </button>
          )}
        </div>

        {showHint && (
          <HintUI 
            question={currentQ.question} 
            options={currentQ.options}
            onClose={() => setShowHint(false)}
          />
        )}

        {showResult && evaluation && (
          <div className={`p-6 rounded-2xl border-2 ${
            evaluation.is_correct 
              ? 'bg-green-500/20 border-green-400' 
              : 'bg-red-500/20 border-red-400'
          }`}>
            <h3 className={`font-bold text-xl mb-4 flex items-center gap-2 ${
              evaluation.is_correct ? 'text-green-400' : 'text-red-400'
            }`}>
              {evaluation.is_correct ? '‚úÖ Correct!' : '‚ùå Incorrect'}
            </h3>
            <p className="text-white mb-4 text-lg leading-relaxed">{evaluation.explanation}</p>
            {evaluation.follow_up && (
              <div className="bg-blue-500/20 p-4 rounded-xl border border-blue-400/50">
                <p className="text-blue-300"><strong className="text-blue-400">üí≠ Follow-up:</strong> {evaluation.follow_up}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default QuizUI;
