import React from 'react';

const ProgressTracker = ({ progress }) => {
  const { current, total, score } = progress;
  const progressPercentage = total > 0 ? (current / total) * 100 : 0;
  const accuracy = current > 0 ? (score / current) * 100 : 0;

  return (
    <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl p-6 mb-6 border border-white/20">
      <h2 className="text-2xl font-bold text-white mb-6 flex items-center gap-2">
        ðŸ“Š Learning Progress
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <div className="text-center bg-gradient-to-br from-blue-500/20 to-cyan-500/20 rounded-2xl p-4 border border-blue-500/30">
          <div className="text-3xl font-bold text-cyan-400">{current}</div>
          <div className="text-sm text-gray-300">Questions Answered</div>
        </div>

        <div className="text-center bg-gradient-to-br from-green-500/20 to-emerald-500/20 rounded-2xl p-4 border border-green-500/30">
          <div className="text-3xl font-bold text-green-400">{score}</div>
          <div className="text-sm text-gray-300">Correct Answers</div>
        </div>

        <div className="text-center bg-gradient-to-br from-purple-500/20 to-pink-500/20 rounded-2xl p-4 border border-purple-500/30">
          <div className="text-3xl font-bold text-purple-400">{accuracy.toFixed(1)}%</div>
          <div className="text-sm text-gray-300">Accuracy</div>
        </div>
      </div>

      <div className="mb-4">
        <div className="flex justify-between text-sm text-gray-300 mb-2">
          <span className="flex items-center gap-1">
            ðŸŽ¯ Progress
          </span>
          <span>{current} / {total}</span>
        </div>
        <div className="w-full bg-gray-700/50 rounded-full h-3 overflow-hidden">
          <div 
            className="bg-gradient-to-r from-cyan-400 to-purple-400 h-3 rounded-full transition-all duration-500 ease-out"
            style={{ width: `${progressPercentage}%` }}
          ></div>
        </div>
      </div>

      {current > 0 && (
        <div className="mt-4 p-4 bg-gradient-to-r from-indigo-500/20 to-purple-500/20 rounded-2xl border border-indigo-500/30">
          <div className="text-white text-center">
            <div className="text-2xl mb-2">{
              accuracy >= 80 ? 'ðŸŽ†' :
              accuracy >= 60 ? 'ðŸŽ‰' :
              accuracy >= 40 ? 'ðŸ’ª' :
              'ðŸŽ¯'
            }</div>
            <div className="font-semibold">{
              accuracy >= 80 ? 'Outstanding Performance!' :
              accuracy >= 60 ? 'Great Job!' :
              accuracy >= 40 ? 'Keep Going!' :
              'You\'ve Got This!'
            }</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProgressTracker;
