import React, { useState } from 'react';
import axios from 'axios';

const Home = ({ onStartQuiz }) => {
  const [transcript, setTranscript] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);

  const handleVideoUpload = async (file) => {
    if (!file) return;

    setUploading(true);
    const formData = new FormData();
    formData.append('video', file);
    if (transcript) formData.append('transcript', transcript);

    try {
      const response = await axios.post('http://localhost:8000/upload_video', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      onStartQuiz(response.data.transcript);
    } catch (error) {
      console.error('Video upload failed:', error);
    } finally {
      setUploading(false);
    }
  };

  const handleUrlSubmit = async () => {
    if (!videoUrl.trim()) return;

    setUploading(true);
    try {
      const response = await axios.post('http://localhost:8000/process_video_url', {
        url: videoUrl
      });
      onStartQuiz(response.data.transcript);
    } catch (error) {
      console.error('Video URL processing failed:', error);
    } finally {
      setUploading(false);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleVideoUpload(e.dataTransfer.files[0]);
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Animated background */}
      <div className="absolute inset-0 bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-blue-500/10 rounded-full blur-3xl animate-float"></div>
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-float" style={{animationDelay: '2s'}}></div>
      </div>

      <div className="relative z-10 container mx-auto px-4 py-12">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-16 animate-slide-up">
            <div className="inline-flex items-center gap-4 mb-6">
              <div className="text-7xl animate-float">🧠</div>
              <h1 className="text-7xl font-black bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">
                AI Learning Hub
              </h1>
            </div>
            <p className="text-2xl text-gray-300 font-light max-w-2xl mx-auto leading-relaxed">
              Transform any video into an <span className="text-blue-400 font-semibold">intelligent learning experience</span> with AI-powered quizzes
            </p>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl p-8 border border-white/20">
            <div className="grid md:grid-cols-3 gap-8">
              {/* Video Upload */}
              <div className="space-y-6">
                <h2 className="text-2xl font-bold text-white flex items-center gap-2">
                  🎥 Upload Video
                </h2>

                <div
                  className={`border-2 border-dashed rounded-2xl p-8 text-center transition-all duration-300 ${
                    dragActive ? 'border-cyan-400 bg-cyan-400/10' : 'border-gray-400 hover:border-cyan-400'
                  }`}
                  onDragEnter={handleDrag}
                  onDragLeave={handleDrag}
                  onDragOver={handleDrag}
                  onDrop={handleDrop}
                >
                  {uploading ? (
                    <div className="text-cyan-400">
                      <div className="animate-spin w-12 h-12 border-4 border-cyan-400 border-t-transparent rounded-full mx-auto mb-4"></div>
                      <p>Processing video...</p>
                    </div>
                  ) : (
                    <>
                      <div className="text-6xl mb-4">📁</div>
                      <p className="text-white mb-4">Drag & drop your video here</p>
                      <input
                        type="file"
                        accept="video/*"
                        onChange={(e) => handleVideoUpload(e.target.files[0])}
                        className="hidden"
                        id="video-upload"
                      />
                      <label
                        htmlFor="video-upload"
                        className="bg-gradient-to-r from-cyan-500 to-purple-500 text-white px-6 py-3 rounded-xl cursor-pointer hover:shadow-lg transition-all duration-300 inline-block"
                      >
                        Choose Video File
                      </label>
                    </>
                  )}
                </div>
              </div>

              {/* Video URL */}
              <div className="space-y-6">
                <h2 className="text-2xl font-bold text-white flex items-center gap-2">
                  🌐 Online Video URL
                </h2>

                <input
                  type="url"
                  value={videoUrl}
                  onChange={(e) => setVideoUrl(e.target.value)}
                  placeholder="https://youtube.com/watch?v=..."
                  className="w-full p-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-400 focus:border-transparent backdrop-blur-sm"
                />

                <button
                  onClick={handleUrlSubmit}
                  disabled={!videoUrl.trim() || uploading}
                  className="w-full bg-gradient-to-r from-green-500 to-blue-500 text-white px-6 py-3 rounded-xl hover:shadow-lg transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Process Online Video
                </button>
              </div>

              {/* Transcript Input */}
              <div className="space-y-6">
                <h2 className="text-2xl font-bold text-white flex items-center gap-2">
                  📝 Or Provide Transcript
                </h2>

                <textarea
                  value={transcript}
                  onChange={(e) => setTranscript(e.target.value)}
                  placeholder="Paste existing transcript here (optional)..."
                  className="w-full h-40 p-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-400 focus:border-transparent backdrop-blur-sm"
                />

                <button
                  onClick={() => onStartQuiz(transcript)}
                  disabled={!transcript.trim()}
                  className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-xl hover:shadow-lg transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Generate Quiz from Text
                </button>
              </div>
            </div>
          </div>

          <div className="mt-8 grid md:grid-cols-3 gap-6">
            {[
              { icon: '🎯', title: 'Smart Quizzes', desc: 'AI generates personalized questions' },
              { icon: '💡', title: 'Instant Hints', desc: 'Get help when you need it' },
              { icon: '📊', title: 'Progress Tracking', desc: 'Monitor your learning journey' }
            ].map((feature, idx) => (
              <div key={idx} className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20 text-center">
                <div className="text-4xl mb-3">{feature.icon}</div>
                <h3 className="text-xl font-bold text-white mb-2">{feature.title}</h3>
                <p className="text-gray-300">{feature.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
