import React, { useState, useEffect, useCallback } from 'react';
import QuizUI from '../components/QuizUI';
import ProgressTracker from '../components/ProgressTracker';
import axios from 'axios';

const QuizPage = ({ transcript, onBackHome }) => {
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [progress, setProgress] = useState({ current: 0, total: 0, score: 0 });

  const generateQuiz = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:8000/generate_quiz', {
        transcript: transcript,
        learner_state: 'engaged',
        difficulty: 'medium'
      });

      setQuiz(response.data.quiz);
      setProgress(prev => ({ ...prev, total: response.data.quiz.length }));
    } catch (err) {
      setError('Failed to generate quiz. Please try again.');
      console.error('Quiz generation error:', err);
    } finally {
      setLoading(false);
    }
  }, [transcript]);

  useEffect(() => {
    generateQuiz();
  }, [generateQuiz]);

  const handleQuizComplete = (finalScore) => {
    setProgress(prev => ({ ...prev, score: finalScore }));
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin w-16 h-16 border-4 border-cyan-400 border-t-transparent rounded-full mx-auto mb-6"></div>
          <div className="text-2xl text-white font-semibold">üß† Crafting your personalized quiz...</div>
          <div className="text-gray-300 mt-2">AI is analyzing the content</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center">
        <div className="bg-red-500/20 backdrop-blur-lg border border-red-500/30 rounded-3xl p-8 max-w-md text-center">
          <div className="text-6xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-2xl font-bold text-white mb-4">Oops! Something went wrong</h2>
          <p className="text-gray-300 mb-6">{error}</p>
          <button
            onClick={onBackHome}
            className="bg-gradient-to-r from-red-500 to-pink-500 text-white px-6 py-3 rounded-xl hover:shadow-lg transition-all duration-300"
          >
            üè† Back to Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
              üéØ Adaptive Quiz Challenge
            </h1>
            <button
              onClick={onBackHome}
              className="bg-white/10 backdrop-blur-lg text-white px-6 py-3 rounded-xl border border-white/20 hover:bg-white/20 transition-all duration-300"
            >
              üè† Home
            </button>
          </div>

          <ProgressTracker progress={progress} />

          {quiz && (
            <QuizUI 
              questions={quiz} 
              onComplete={handleQuizComplete}
              onProgressUpdate={setProgress}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default QuizPage;
