import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const HintUI = ({ question, options, onClose }) => {
  const [hint, setHint] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchHint = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:8000/generate_hint', {
        question: question,
        options: options
      });

      setHint(response.data.hint);
    } catch (err) {
      setError('Failed to generate hint. Please try again.');
      console.error('Hint generation error:', err);
    } finally {
      setLoading(false);
    }
  }, [question, options]);

  useEffect(() => {
    fetchHint();
  }, [fetchHint]);

  return (
    <div className="fixed inset-0 bg-black/70 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white/10 backdrop-blur-lg rounded-3xl p-8 max-w-lg w-full border border-white/20 shadow-2xl">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-2xl font-bold text-yellow-400 flex items-center gap-2">
            üí° Smart Hint
          </h3>
          <button
            onClick={onClose}
            className="text-white/60 hover:text-white text-3xl transition-colors duration-200 hover:rotate-90 transform transition-transform"
          >
            √ó
          </button>
        </div>

        {loading ? (
          <div className="text-center py-8">
            <div className="animate-pulse flex items-center justify-center gap-3">
              <div className="w-3 h-3 bg-yellow-400 rounded-full animate-bounce"></div>
              <div className="w-3 h-3 bg-yellow-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
              <div className="w-3 h-3 bg-yellow-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
            </div>
            <div className="text-white mt-4">AI is thinking...</div>
          </div>
        ) : error ? (
          <div className="text-red-400 py-6 text-center">
            <div className="text-4xl mb-2">‚ö†Ô∏è</div>
            <div>{error}</div>
          </div>
        ) : (
          <div className="bg-gradient-to-r from-yellow-500/20 to-orange-500/20 border border-yellow-400/30 rounded-2xl p-6 mb-6">
            <div className="flex items-start gap-3">
              <div className="text-2xl">üßê</div>
              <p className="text-white text-lg leading-relaxed">{hint}</p>
            </div>
          </div>
        )}

        <div className="flex justify-end">
          <button
            onClick={onClose}
            className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white px-6 py-3 rounded-xl font-semibold hover:shadow-lg transition-all duration-300 flex items-center gap-2"
          >
            ‚ú® Got it!
          </button>
        </div>
      </div>
    </div>
  );
};

export default HintUI;
